        public long MaxRunTime(int n, int[] batteries)
        {
            long sum = 0;
            for (int i = 0; i < batteries.Length; i++)
                sum += batteries[i];

            long start = 1, end = sum, maxMinutes = sum;

            while (start <= end)
            {
                long time = (end - start) / 2 + start;
                long duration = 0;
                for (int i = 0; i < batteries.Length; i++)
                {
                    if (batteries[i] <= time)
                        duration += batteries[i];
                    else duration += time;
                }

                if (time > duration / n) // means avg for any 'n' computers run time is less than capped time. Got to search for a smaller number
                    end = time - 1;
                else
                {
                    //search to the right, and set maxMinutes as this is a valid time to have all computers powered
                    maxMinutes = time;
                    start = time + 1;
                }
            }

            return maxMinutes;
        }

        public long MaxRunTimeStable(int n, int[] batteries)
        {
            var maxMinutes = 0;

            var list = new List<int>(batteries);
            list.Sort();
            list.Reverse();

            while (list.Count >= n && list[n - 1] > 0)
            {
                var len = list.Count;

                // reduced list
                var newList = new List<int>(list.GetRange(n, len - n));

                var subtract = 1;
                for (var i = 0; i < n; i++)
                {
                    list[i] -= subtract;
                    if (list[i] > 0)
                        newList.Insert(0, list[i]);
                }

                maxMinutes += subtract;
                list = newList;
            }

            return maxMinutes;
        }
    }
