        public void print(int[][] mat)
        {
            for (int i = 0; i < mat.Length; i++)
            {
                for (int j = 0; j < mat[0].Length; j++)
                    Console.Write(mat[i][j]);

                Console.WriteLine();
            }
        }

        public int[][] UpdateMatrix(int[][] mat)
        {
            int m = mat.Length;
            int n = mat[0].Length;
            var result = new int[m][];

            List<Tuple<int, int>> bases = new List<Tuple<int, int>>();
            for (int i = 0; i < mat.Length; i++)
            {
                result[i] = new int[n];
                for (int j = 0; j < mat[0].Length; j++)
                    if (mat[i][j] == 0)
                    {
                        result[i][j] = 0;
                        bases.Add(Tuple.Create(i, j));
                    }
                    else
                        result[i][j] = int.MaxValue;
            }

            bool loop = true;
            var increment = 0;
            while (loop)
            {
                loop = false;
                increment++;
                foreach (var tuple in bases)
                    for (int i = -1 * increment; i <= increment; i++)
                    {
                        var j = i <= 0 ? increment + i : increment - i;
                        for (int k = -1; k <= 1; k += 2)
                        {
                            var x = tuple.Item1 + i;
                            var y = tuple.Item2 + (k * j);
                            if (0 <= x && x < m && 0 <= y && y < n
                                && result[x][y] > increment)
                            {
                                result[x][y] = increment;
                                //at least one element was updated
                                loop = true;
                            }
                        }
                    }
            }

            return result;
        }

        public bool WordPattern(string pattern, string s)
        {
            var dict = new Dictionary<char, string>();
            foreach (char c in pattern)
            {
                if (s.Length <= 0) return false;

                var len = s.IndexOf(" ");
                var word = s.Substring(0, len < 0 ? s.Length : len);
                if (dict.ContainsKey(c))
                {
                    if (dict[c] != word)
                        return false;
                }
                else if (dict.ContainsValue(word)) return false;
                else
                    dict.Add(c, word);

                s = s.Substring(len > 0 ? len : s.Length).Trim();
            }

            if (s.Length > 0)
                return false;

            return true;
        }
    }
